export default function App() {
  const [searchTerm, setSearchTerm] = useState('');
  const [filterCategory, setFilterCategory] = useState('');

  const filteredProducts = initialProducts.filter(product =>
    product.name.toLowerCase().includes(searchTerm.toLowerCase()) &&
    (filterCategory === '' || product.category === filterCategory)
  );

  return (
    <div className="app-container">
      <nav className="navbar">
        <div className="navbar-left">
          <h1>My App</h1>
          <ul className="nav-links">
            <li><a href="#">Home</a></li>
            <li><a href="#">About</a></li>
            <li><a href="#">Contact</a></li>
          </ul>
        </div>
        <div className="navbar-right">
          <input
            type="text"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            placeholder="Search..."
          />
          <button>Search</button>
        </div>
      </nav>

      <div className="filter-buttons">
        <button className="fruit-btn" onClick={() => setFilterCategory('fruit')}>Fruits</button>
        <button className="veg-btn" onClick={() => setFilterCategory('vegetable')}>Vegetables</button>
        <button className="healthy-btn" onClick={() => setFilterCategory('healthy')}>Healthy Food</button>
        <button className="all-btn" onClick={() => setFilterCategory('')}>All Products</button>
      </div>

      <section className="product-section">
        <h2>Products</h2>
        <div className="product-grid">
          {filteredProducts.map((product, index) => (
            <div key={index} className="product-card">
              <img src={product.image} alt={product.name} />
              <h3>{product.name}</h3>
              <p>{product.description}</p>
              <p className="price">{product.price}</p>
            </div>
          ))}
          {filteredProducts.length === 0 && (
            <p className="no-match">No matching products found.</p>
          )}
        </div>
      </section>

      <footer className="footer">
        <h3>Our Address</h3>
        <p>123 Healthy Street, Wellness City, WC 45678</p>
        <img src="location.png" alt="Address Map"/>
      </footer>
    </div>
  );
}
